import React from "react";




class Todo extends React.Component {
    constructor(props) {
      super(props);
      this.state = {
        term: "",
        items: [],
        completed: {},
      };
      this.handleRemove = this.handleRemove.bind(this);
      this.handleCheck = this.handleCheck.bind(this);
    }

    onChange = event => {
      this.setState({ term: event.target.value });
    };

    onSubmit = event => {
      event.preventDefault();
      if (this.state.term.length < 34) {
        this.setState({
          term: "",
          items: [...this.state.items, this.state.term]
        });
      } else {
        alert("limit to 33 characters");
      }
    };

    handleRemove(index, event) {
      if (("are you sure?")) {
        let todoArray = this.state.items;
        todoArray.splice(index, 1);
        this.setState({
          items: todoArray
        });
      }
    }
    handleCheck(index, event) {
      // code to create line through completed item
      this.setState(state => ({
        completed: { ...state.completed, [index]: !state.completed[index] }
      }));
    }

    render() {
      return (
        <div className="container">
         <h1>All Tasks</h1>
        <form>
        <div>
           <input id="input" type="text" name="add" class="textinput" placeholder="Add a new task"  value ={this.state.term} onChange={this.onChangeHandler} />
           <button id="addbtn" class="button" onClick={this.onAddhandler}>Add</button>

        </div>
        </form>

        <div>
            {this.state.items.length === 0 && <h2 id="h2">Seems like a quiet day</h2>}
        </div>
          <ul class="list-group">
            {this.state.items.map((item, index) => (
              <li
                style={{
                  textDecoration: this.state.completed[index]
                    ? "line-through"
                    : ""
                }}
                key={index}
              >
                <input
                  id="chk"
                  onChange={() => this.handleCheck(index)}
                  type="checkbox"
                />{" "}
                {item}
                <button
                  className="btn btn-info"
                  onClick={event => this.handleRemove(index, event)}
                >
                  X
                </button>
              </li>
            ))}
          </ul>
        </div>
      );
    }
  }


  export default  Todo
